		    , email
			, description
			, create_date
			, create_by
			, status
		)
		VALUES (
		#{id}
		, #{name}
		, #{address}
		, #{contact}
		, #{other_contact}
		, #{email}
		, COALESCE(#{description}, '' )
		, to_char(NOW(), 'YYYYMMDD')
		, #{user_id}
		, #{status}
		)

mapper.update:

UPDATE vendor
SET
	  name 			= #{name}
