mapper.insert:

    	INSERT INTO card_identify
		(
		    id
		    , card_id
		    , card_img_front
		    , card_img_rear
		    , create_by
		    , create_date
		    , status
		)
		VALUES (
		#{id}
		, #{card_id}
		, #{font_image_id}
		, #{rear_image_id}
		, #{user_id}
		, to_char(NOW(), 'YYYYMMDD')
		, #{status}
		)

mapper.update:

		UPDATE card_identify
		SET
             card_id            = #{card_id}
            , card_img_front    = #{font_image_id}
            , card_img_rear     = #{rear_image_id}
            , modify_by         = #{user_id}
            , modify_date       = to_char(NOW(), 'YYYYMMDD')
            , status            = #{status}
		WHERE id 			    = #{id}


		update card_identify
        set
             status  		= #{status}
            , modify_by   	= #{user_id}
            , modify_date	= to_char(NOW(), 'YYYYMMDD')
        where id   			= #{id}
