mapper.insert:

    	INSERT INTO category
		(
		    id
		    , name
			, description
			, create_date
			, create_by
			, status
		)
		VALUES (
		#{id}
		, #{name}
		, COALESCE(#{description}, '' )
		, to_char(NOW(), 'YYYYMMDD')
		, #{user_id}
		, #{status}
		)

mapper.update:

UPDATE category
SET
	  name 			= #{name}
	, description 	= COALESCE(#{description},'')
	, status		= #{status}
	, modify_by		= #{user_id}
	, modify_date	= to_char(NOW(), 'YYYYMMDD')
WHERE id 			= #{id}


		update category
        set
             status  		= #{status}
            , modify_by   	= #{user_id}
            , modify_date	= to_char(NOW(), 'YYYYMMDD')
        where id   			= #{id}
