mapper.insert:

    	INSERT INTO main_category
		(
			 "name"
			, status
			, create_by
			, create_date
			, description
		)
		VALUES(
				 #{name}
				, #{status}
				, #{userID}
				, to_char(NOW(), 'YYYYMMDD')
				, COALESCE(#{description}, '' )
		);

mapper.update:

UPDATE main_category
SET name		= 	#{name}
, status		=	#{status}
, modify_by		=	#{userID}
, modify_date	=	to_char(NOW(), 'YYYYMMDD')
, description	=	 COALESCE(#{description}, '' )
WHERE id		=	#{id};


		update main_category
        set
             status  		= #{status}
