mapper.update:

UPDATE resource_img
SET
	  id 			= #{uuid}
	, product_id 	= #{product_id}
	, file_source   = #{file_source}
	, file_name      = #{file_name}
	, file_size      = #{file_size}
	, file_extension = #{file_extension}
	, status		= #{status}
	, modify_by		= #{user_id}
	, modify_date	= to_char(NOW(), 'YYYYMMDD')
WHERE id 			= #{uuid}
