mapper.update:

UPDATE user_
SET
	account_expired 		= 	#{accountExpired}
	, account_locked		=	#{accountLocked}
	, credentials_expired	=	#{credentialsExpired}
	, enabled				=	#{enabled}
	, status				=	#{status}
	, modify_date			=	to_char(NOW(), 'YYYYMMDD')
	, modify_by				=	#{userID}
WHERE id					=	#{id};

