	, to_char(NOW(), 'YYYYMMDD')
	, to_char(NOW(), 'HH12MI')
	, #{status}
	, #{message}
	);

mapper.update:

UPDATE user_lock_count
	SET
	 count		=	#{count}
	, date		=	to_char(NOW(), 'YYYYMMDD')
	, time 		= 	to_char(NOW(), 'HH12MI')
	, status	=	#{status}
	, message	=	#{message}
	, islocked	=	#{isLocked}
WHERE id		= 	#{id}
AND	islocked	= 	#{isLocked}


UPDATE user_lock_count
